import React from 'react'
import { Paper, Avatar } from '@mui/material'
import ChatTopStyle from './ChatTopStyle';

const ChatMiddle = ({ messages }) => {
    const classes = ChatTopStyle();
    return (
        <>
            <Paper position="fixed" sx={{ height: "75%", margin: "2vh", border: 'none', boxShadow: "none", bgcolor: "transparent" }}>
                <div className={classes.Middle}>
                    {messages.map((message, index) => {
                        return (
                            <div key={index} className={messages.fromself ? classes.Sender : classes.Receiver}>
                                <p>{message.message}</p>
                            </div>
                        );
                    })}
                </div>
            </Paper>
        </>

    )
}

export default ChatMiddle
