import React, { useState } from 'react'
import { List, ListItem, ListItemText, ListItemAvatar, Typography, Avatar, Divider } from '@mui/material'

const Contacts = ({ contacts, current, setChatter }) => {
    const [currentSelected, setCurrentSelected] = useState(null);

    const changeCurrentChat = (index, contact) => {
        setCurrentSelected(index);
        setChatter(contact);
    };

    return (
        <List sx={{ width: "100%", bgcolor: "white" }}>
            {contacts.map((contact, index) => {
                return (
                    <div
                        key={contact._id}
                        onClick={() => changeCurrentChat(index, contact)}
                        className={`Contact${index === currentSelected ? "Selected" : ""}`}
                    >
                        <ListItem alignItems="flex-start">
                            <ListItemAvatar>
                                <Avatar alt={`${contact.username}`} src={`${contact.avatar}`} />
                            </ListItemAvatar>
                            <ListItemText
                                primary={`${contact.username}`}
                            />
                        </ListItem>
                        <Divider variant="inset" component="li" />
                    </div>
                )
            })}
        </List>
    )
}

export default Contacts
