import React from 'react'
import { Paper, Avatar } from '@mui/material'
import ChatTopStyle from './ChatTopStyle';

const ChatMiddle = ({ messages }) => {
  const classes = ChatTopStyle();
  return (
    <>
      <Paper position="fixed" sx={{ margin: "2vh", border: 'none', bgcolor: "transparent"}}>
        <div className={classes.Middle}>
        {messages.map((message) => {
          return (
            <div>
              <div>
                <div>
                  <p>{message.message}</p>
                </div>
              </div>
            </div>
          );
        })}
        </div>
      </Paper>
    </>

  )
}

export default ChatMiddle
