import React, { useState } from 'react'
import { Paper } from '@mui/material'
import ChatBottomStyle from './ChatBottomStyle';
import { IconButton, Menu, MenuItem } from '@mui/material';
import Picker from 'emoji-picker-react'
import SendIcon from '@mui/icons-material/Send';
import EmojiEmotionsIcon from '@mui/icons-material/EmojiEmotions';
import EmojiEmotions from '@mui/icons-material/EmojiEmotions';

const ChatBottom = ({ currentChatter }) => {
    const classes = ChatBottomStyle();
    const [message, setMessage] = useState("");

    const [anchorEl, setAnchorEl] = React.useState(null);
    const open = Boolean(anchorEl);
    const handleEmojiClick = (event) => {
        setAnchorEl(event.currentTarget);
    };
    const handleClose = () => {
        setAnchorEl(null);
    };
    const handleEmoji = (event, emojiObject) => {
        let msg = message;
        msg += emojiObject.emoji;
        setMessage(msg);
        handleClose();
      };

    return (
        <div>
            <Paper position="fixed" sx={{ bgcolor: "white", margin: "2vh", padding: "2px", borderRadius: "30px" }}>
                <div className={classes.Bottom}>
                    <IconButton edge="end" onClick={handleEmojiClick}>
                        <EmojiEmotions />
                    </IconButton>
                    <Menu id="basic-menu"
                        anchorEl={anchorEl}
                        open={open}
                        onClose={handleClose}
                        PaperProps={{
                            style: {
                                transform: 'translateX(30px) translateY(-8vh)',
                            }
                        }}
                        >
                            <Picker onEmojiClick={handleEmoji} />
                    </Menu>
                </div>
            </Paper>
        </div>

    )
}

export default ChatBottom
