import { makeStyles } from "@mui/styles";

const personalStyle = makeStyles({
    Overall:{
        verticalAlign: "middle",
    },
    Box: {
        margin: "0",
        padding: "15%",
        height: "700px",
        width: "650px",
        boxShadow: "5px 10px 20px #000",
        display: "flex",
        flexDirection: "column",
        justifyContent: "center",
        borderRadius: "30px",
        userSelect: "none",
        background: "#fff",
        gap: "1rem",
    },
    AvatarDiv:{
        display: "flex",
        justifyContent: "space-between",
        alignItems: "center",
    },
    AboutMeDiv: {
        height: "40%",
    },
    AboutMe: {
        width: "95%",
        height: "95%",
        background: "#e0dede",
        justifyContent: "center",
        display: "flex",
        padding: "10px",
        border: "none",
        outline: "none",
        borderRadius: "20px",
        fontSize: "16px",
        resize: "none",
    },

})

export default personalStyle
