import React from 'react'
import { Paper, Avatar, Typography } from '@mui/material'
import ChatMiddleStyle from './ChatMiddleStyle';

const ChatMiddle = ({ messages }) => {
    const classes = ChatMiddleStyle();
    return (
        <>
            <Paper position="fixed" sx={{ height: "75%", margin: "2vh", border: 'none', boxShadow: "none", bgcolor: "transparent" }}>
                <div className={classes.Middle}>
                    {messages.map((message, index) => {
                        return (
                            messages.fromSelf?
                            <div className={classes.Sender}>
                                <Paper sx={{ padding: "1vh" }}
                                    key={index} >
                                    <Typography>{message.message}</Typography>
                                </Paper>
                            </div>
                            :
                            <div className={classes.Receiver}>
                                <Paper sx={{ padding: "1vh" }}
                                    key={index} >
                                    <Typography>{message.message}</Typography>
                                </Paper>
                            </div>
                        );
                    })}
                </div>
            </Paper>
        </>

    )
}

export default ChatMiddle
