import { makeStyles } from "@mui/styles";


const ChatMiddleStyle = makeStyles({
    Middle: {
        display: "flex",
        flexDirection: "column",
        margin: "1vh",
        alignItems: "center",
        justifyContent: "space-between"
    },
    Sender: {
        width: "100%",
        margin: "1vh",
        display: "flex",
        flexDirection: "row",
        justifyContent: "flex-end",
        gap: "1vw",
    },
    Receiver: {
        width: "100%",
        margin: "1vh",
        display: "flex",
        flexDirection: "row",
        justifyContent: "flex-start",
        gap: "1vw",
    }
});

export default ChatMiddleStyle;
