import React from 'react'
import PermIdentityIcon from '@mui/icons-material/PermIdentity';
import { Paper, Avatar, IconButton, Typography, Menu, Card, CardContent, Divider } from '@mui/material'
import ChatTopStyle from './ChatTopStyle';
import FemaleIcon from '@mui/icons-material/Female';
import MaleIcon from '@mui/icons-material/Male';
import TransgenderIcon from '@mui/icons-material/Transgender';

const ChatTop = ({ currentChatter }) => {
  const classes = ChatTopStyle();

  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);
  const handleButtonClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };

  const sex = (sex) => {
    if(sex === "Male"){
        return <MaleIcon fontSize='small'/>
    }
    else if(sex === "Female"){
        return <FemaleIcon fontSize='small'/>
    }
    else{
        return <TransgenderIcon fontSize='small'/>
    }
}

  return (
    <>
      <Paper position="fixed" sx={{ bgcolor: "white", margin: "2vh 2vw 0 2vw" }}>
        <div className={classes.Top}>
          <div className={classes.AvatarName}>
            <Avatar alt={`${currentChatter.username}`} src={`${currentChatter.avatar}`} />
            <Typography>{currentChatter.username}</Typography>
          </div>
          <IconButton edge="end" onClick={handleButtonClick}>
            <PermIdentityIcon />
          </IconButton>
          <Menu id="basic-menu"
            anchorEl={anchorEl}
            open={open}
            onClose={handleClose}
            PaperProps={{
              style: {
                transform: 'translateX(-2.5vw) translateY(3vh)',
              }
            }}
          >
            <Card sx={{width: "300px", userSelect: "none"}}>
              <CardContent>
                <div className={classes.UserInfo}>
                  <Avatar alt={`${currentChatter.username}`} src={`${currentChatter.avatar}`} />
                  <Typography variant="h6">{currentChatter.username}</Typography>
                </div>
                <Typography variant="h1">{sex(currentChatter.gender)}</Typography>
                <Typography variant="h6">About Me:</Typography>
                {(currentChatter.aboutme === "") ? 
                <Typography color="text.secondary" sx={{wordWrap: "break-word", marginTop: "1vh"}}>This user is lazy to leave anything...</Typography>
                :
                <Typography sx={{wordWrap: "break-word", marginTop: "1vh"}}>{currentChatter.aboutme}</Typography>
              }
              </CardContent>
            </Card>
          </Menu>
        </div>
      </Paper>
    </>

  )
}

export default ChatTop
