import React from 'react'
import PermIdentityIcon from '@mui/icons-material/PermIdentity';
import { Paper, Avatar, IconButton, Typography, Menu, Card, CardContent } from '@mui/material'
import ChatTopStyle from './ChatTopStyle';

const ChatTop = ({ currentChatter }) => {
  const classes = ChatTopStyle();

  const [anchorEl, setAnchorEl] = React.useState(null);
  const open = Boolean(anchorEl);
  const handleButtonClick = (event) => {
    setAnchorEl(event.currentTarget);
  };
  const handleClose = () => {
    setAnchorEl(null);
  };

  return (
    <>
      <Paper position="fixed" sx={{ bgcolor: "white", margin: "2vh 2vw 0 2vw" }}>
        <div className={classes.Top}>
          <div className={classes.AvatarName}>
            <Avatar alt={`${currentChatter.username}`} src={`${currentChatter.avatar}`} />
            <Typography>{currentChatter.username}</Typography>
          </div>
          <IconButton edge="end" onClick={handleButtonClick}>
            <PermIdentityIcon />
          </IconButton>
          <Menu id="basic-menu"
            anchorEl={anchorEl}
            open={open}
            onClose={handleClose}
            PaperProps={{
              style: {
                transform: 'translateX(-30px) translateY(3vh)',
              }
            }}
          >
            <Card sx={{width: "250px"}}>
              <CardContent>
                <div className={classes.AvatarName}>
                  <Avatar alt={`${currentChatter.username}`} src={`${currentChatter.avatar}`} />
                  <Typography>{currentChatter.username}</Typography>
                  <Typography>{currentChatter.gender}</Typography>
                </div>
              </CardContent>
            </Card>
          </Menu>
        </div>
      </Paper>
    </>

  )
}

export default ChatTop
