import React, { useState } from 'react'
import { List, ListItem, ListItemText, ListItemAvatar, Typography, Avatar } from '@mui/material'

const Contacts = ({ contacts, current, currentChatter }) => {
    const [currentSelected, setCurrentSelected] = useState(null);

    const changeCurrentChat = (index, contact) => {
        setCurrentSelected(index);
        currentChatter(contact);
        console.log(index)
    };

    return (
        <List sx={{ width: "100%", bgcolor: "white" }}>
            {contacts.map((contact, index) => {
                return (<ListItem alignItems="flex-start" onClick={() => changeCurrentChat(index, contact)}>
                    <ListItemAvatar>
                        <Avatar alt={`${contact.username}`} src={`${contact.avatar}`} />
                    </ListItemAvatar>
                    <ListItemText
                        primary={`${contact.username}`}
                    />
                </ListItem>
            )
            })}
        </List>
    )
}

export default Contacts
