import React, { useState, useEffect } from 'react'
import ChatStyle from './ChatStyle'
import axios from 'axios';
import { userContactRoute } from '../api/ApiRoutes';
import Contacts from '../components/Contacts';

const Chat = () => {
  const classes = ChatStyle();
  const [contacts, setContacts] = useState([]);
  const [current, setCurrent] = useState(null);

  useEffect(() => {
    async function fetchCurrent() {
      const data = await JSON.parse(localStorage.getItem('chat-app-user-logined'))
      setCurrent(data);
    }
    fetchCurrent()
    }, []);

  useEffect(() => {
    async function fetchContact() {
      if (current) {
        const data = await axios.get(`${userContactRoute}/${current._id}`)
        setContacts(data.data);
      }
    };
    fetchContact()
  }, [current])

  return (
    <div className={classes.Container}>
      <Contacts contacts = {contacts} current = {current}>

      </Contacts>
    </div>
  )
}

export default Chat
