import { makeStyles } from "@mui/styles";


const ChatMiddleStyle = makeStyles({
    Middle: {
        display: "flex",
        flexDirection: "column",
        margin: "1vh",
        alignItems: "center",
        justifyContent: "space-between"
    },
    Sender: {
        margin: "1vh",
        justifyContent: "flex-end",
    }
});

export default ChatMiddleStyle;
